parameters.functionCode:
// Extract and validate input
const body = $input.item.json.body;

return {
  session_id: body.session_id || '',
  message: body.message || '',
  user_id: body.user_id || body.session_id,
  timestamp: body.timestamp || new Date().toISOString()
};

// Format context from Qdrant results
const results = $input.item.json.result || [];

const context = results.map((r, i) => {
  return `[${i+1}] Fonte: ${r.payload.source_file}\n${r.payload.text}`;
}).join('\n\n');

return { context };

// Format chat history for GPT prompt
const history = $input.item.json;

if (!history || !Array.isArray(history)) {
  return { formatted_history: 'Nessuna conversazione precedente.' };
}

const formatted = history.reverse().map(msg => {
  return `${msg.role === 'user' ? 'Utente' : 'Assistente'}: ${msg.content}`;
}).join('\n');

return { formatted_history: formatted };

